export const renderTicketToHTML = () => {
  // Get current date and time
  const bookingDate = new Date().toLocaleDateString();
  const bookingTime = new Date().toLocaleTimeString();

  // Generate a random transaction ID
  const transactionId = Math.random()
    .toString(36)
    .substring(2, 12)
    .toUpperCase();

  // Create HTML ticket template directly
  return `
    <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto; border: 1px solid #ccc; padding: 20px; border-radius: 5px;">
      <h1 style="color: #3399cc; text-align: center;">ParkEz Booking Confirmation</h1>
      <div style="margin: 20px 0; padding: 15px; background-color: #f5f5f5; border-radius: 5px;">
        <p><strong>Date:</strong> ${bookingDate}</p>
        <p><strong>Time:</strong> ${bookingTime}</p>
        <p><strong>Status:</strong> Confirmed</p>
        <p><strong>Location:</strong> Ajeenkya DY Patil Parking Zone</p>
        <p><strong>Transaction ID:</strong> ${transactionId}</p>
      </div>
      <p style="text-align: center; color: #666;">Please show this confirmation when arriving at the parking spot.</p>
      <p style="text-align: center; color: #666;">Thank you for using ParkEz!</p>
    </div>
  `;
};
